mapper.insert:
INSERT INTO TBL_MEMBER (
    ID, MEMBER_NAME, MEMBER_EMAIL, MEMBER_NICKNAME, MEMBER_PHONE, MEMBER_PASSWORD,
    MEMBER_GENDER, MEMBER_BIRTH
)
VALUES (
           SEQ_MEMBER.NEXTVAL, #{memberName}, #{memberEmail}, #{memberNickName}, #{memberPhone},
           #{memberPassword}, #{memberGender}, #{memberBirth}
       )

mapper.select:
SELECT COUNT(*)
FROM TBL_MEMBER
WHERE MEMBER_NICKNAME = #{nickname}
