mapper.select:
SELECT P2.ID, TEAM_ID, TEAM_NAME, CITY, LOCAL_CITY, T.CREATED_DATE, END_DATE, POST_READ_COUNT, P2.CREATED_DATE, P2.UPDATED_DATE FROM ( SELECT ROWNUM R, ID, TEAM_ID, TEAM_NAME, CITY, LOCAL_CITY, T.CREATED_DATE, END_DATE, POST_READ_COUNT, P1.CREATED_DATE, P1.UPDATED_DATE FROM ( SELECT P.ID, TEAM_ID, T.TEAM_NAME, T.CITY, T.LOCAL_CITY, T.CREATED_DATE, P.END_DATE, P.POST_READ_COUNT, P.CREATED_DATE, P.UPDATED_DATE FROM TBL_TEAM T JOIN TBL_TEAM_POST P ON T.ID = P.TEAM_ID
ORDER BY
ID POST_READ_COUNT
) P1
WHERE ROWNUM <= #{pagination.endRow}) P2 ON P2.R >= #{pagination.startRow}
